import { createSlice } from "@reduxjs/toolkit"

const state={
    Loggedin:false,
    username:"Guest",
    usertype:"",
    personname:"",
    user: JSON.parse(sessionStorage.getItem('user')) || null,
    
}
const userslice=createSlice({
name:"userslice",
initialState:state,
reducers:{
    Login:(state,action)=>{
state.Loggedin=true
state.username=action.payload.uname
state.personname=action.payload.pname
state.usertype=action.payload.utype
sessionStorage.setItem('username', JSON.stringify(action.payload));
    },
    Logout:(state)=>{
    state.Loggedin=false
    state.username="Guest"
    state.personname=""
   state.usertype=""
},
}

})
export const{Login,Logout}=userslice.actions
export default userslice.reducer
