import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useNavigate, useSearchParams } from 'react-router-dom';
import Swal from 'sweetalert2';

const Productdetails = () => {
  const [pname, setpname] = useState();
  const [pq, setpq] = useState();
  const [pimg, setpimg] = useState();
  const [pprice, setpprice] = useState();
  const [pdes, setpdes] = useState();
  const navigate = useNavigate();
  const [params] = useSearchParams();
  const idd = params.get("id");

  const { Loggedin, email } = useSelector((state) => state.userslice);

  useEffect(() => {
    details();
  }, []);

  const details = async () => {
    try {
      const res = await fetch(`http://localhost:9000/api/pdetails/${idd}`);
      if (res.ok) {
        const result = await res.json();
        if (result.statuscode === 1) {
          setpimg(result.dd.pimg);
          setpq(result.dd.pq);
          setpname(result.dd.pname);
          setpprice(result.dd.pprice);
          setpdes(result.dd.pdescription);
        }
      }
    } catch (err) {
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: err.message || 'Failed to load product details',
        confirmButtonText: 'OK'
      });
    }
  };

  const addcart = async () => {
    if (!Loggedin) {
      await Swal.fire({
        icon: 'warning',
        title: 'Login Required',
        text: 'Please login to add items to your cart',
        confirmButtonText: 'OK'
      });
      navigate({ pathname: '/login', search: `?idd=${idd}` });
      return;
    }

    try {
      const emaill = email;
      const tprice = pprice * pq;
      const alldata = { pname, pq, pimg, pprice, pdes, emaill, tprice, productid: idd };

      // Check if product is already in user's cart
      const checkRes = await fetch(`http://localhost:9000/api/checkcart?username=${emaill}&idd=${idd}`);
      if (!checkRes.ok) throw new Error(`Failed to check cart: HTTP ${checkRes.status}`);

      const result = await checkRes.json();
      if (!result || typeof result.statuscode !== 'number') throw new Error('Invalid server response format');

      switch (result.statuscode) {
        case 2: // Product already in cart: update quantity
          const updateRes = await fetch("http://localhost:9000/api/updatecart", {
            method: "PUT",
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              username: emaill,
              productid: idd,
              newQuantity: pq,
              pprice // for total price update in backend
            })
          });
          if (!updateRes.ok) throw new Error(`Failed to update quantity: HTTP ${updateRes.status}`);

          const updateResult = await updateRes.json();
          if (updateResult.statuscode === 1) {
            await Swal.fire({
              icon: 'success',
              title: 'Updated',
              text: 'Quantity updated in cart!',
              timer: 2000,
              showConfirmButton: false
            });
          } else {
            throw new Error(updateResult.msg || 'Failed to update quantity');
          }
          break;

        case 3: // Product not in cart: add new product
          const addRes = await fetch("http://localhost:9000/api/addcart", {
            method: "POST",
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(alldata)
          });
          if (!addRes.ok) throw new Error(`Failed to add to cart: HTTP ${addRes.status}`);

          const addResult = await addRes.json();
          if (addResult.statuscode === 1) {
            await Swal.fire({
              icon: 'success',
              title: 'Added',
              text: 'Product added to cart!',
              timer: 2000,
              showConfirmButton: false
            });
          } else {
            throw new Error(addResult.msg || 'Failed to add to cart');
          }
          break;

        default:
          throw new Error(`Unexpected status code: ${result.statuscode}`);
      }

      navigate("/addtocart");
    } catch (err) {
      console.error('Cart operation failed:', err);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: err.message || 'Failed to process cart request',
        confirmButtonText: 'OK'
      });
    }
  };

  return (
    <div>
      <section>
        <div className="collection-wrapper">
          <div className="container">
            <div className="collection-wrapper">
              <div className="row g-4">
                <div className="col-lg-4">
                  <img src={`uploads/${pimg}`} alt="" className="w-100 img-fluid" style={{ height: "500px" }} />
                </div>
                <div className="col-lg-4" style={{ marginLeft: "100px", height: "700px" }}>
                  <h2>Product Name: {pname}</h2>
                  <h3>MRP: â‚¹ {pprice}</h3>
                  <p>{pdes}</p>
                  <p>Quantity: {pq}</p>
                  <button className="btn btn-solid" onClick={addcart}>Add to cart</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Productdetails;
