import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { useNavigate, useSearchParams } from 'react-router-dom'
import Swal from 'sweetalert2'

const Productdetails = () => {
    const [product, setProduct] = useState({
        pname: '',
        pq: 1,
        pimg: '',
        pprice: 0,
        pdes: ''
    })
    const navigate = useNavigate()
    const [params] = useSearchParams()
    const id = params.get("id")

    const { isLoggedIn, user } = useSelector((state) => state.userslice) // Assuming user object contains id
    
    // Set default quantity to 1
    const [quantity, setQuantity] = useState(1)

    useEffect(() => {
        details()
    }, [id])

    const details = async () => {
        try {
            const res = await fetch(`http://localhost:9000/api/pdetails/${id}`)
            if (res.ok) {
                const result = await res.json()
                if (result.statuscode === 1) {
                    setProduct({
                        pimg: result.dd.pimg,
                        pq: result.dd.pq,
                        pname: result.dd.pname,
                        pprice: result.dd.pprice,
                        pdes: result.dd.pdescription
                    })
                }
            }
        } catch (err) {
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: err.message || 'Failed to load product details',
                confirmButtonText: 'OK'
            })
        }
    }

    const handleQuantityChange = (e) => {
        const value = parseInt(e.target.value) || 1
        setQuantity(Math.max(1, Math.min(value, product.pq))) // Ensure quantity is between 1 and available stock
    }

    const addToCart = async () => {
        if (!isLoggedIn) {
            Swal.fire({
                icon: 'warning',
                title: 'Login Required',
                text: 'Please login to add items to your cart',
                confirmButtonText: 'OK'
            }).then(() => {
                navigate("/addtocart", { state: { from: window.location.pathname } })
            })
            return
        }

        try {
            // First check if product already exists in cart
            const checkRes = await fetch(
                `http://localhost:9000/api/checkcart?userid=${user._id}&productid=${id}`
            )
            
            const checkResult = await checkRes.json()
            
            if (checkResult.exists) {
                Swal.fire({
                    icon: 'info',
                    title: 'Already in Cart',
                    text: 'This product is already in your cart. You can update the quantity there.',
                    confirmButtonText: 'OK'
                })
                navigate("/addtocart")
                return
            }

            // Add new item to cart
            const cartData = {
                productname: product.pname,
                productquantity: quantity,
                productprice: product.pprice,
                productimage: product.pimg,
                productdes: product.pdes,
                userid: user._id,  // Using user ID instead of username
                productid: id
            }

            const addRes = await fetch("http://localhost:9000/api/addcart", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}` // If using JWT
                },
                body: JSON.stringify(cartData)
            })

            if (addRes.ok) {
                const result = await addRes.json()
                if (result.statuscode === 1) {
                    await Swal.fire({
                        icon: 'success',
                        title: 'Added to Cart',
                        text: 'Product added to cart successfully!',
                        timer: 2000,
                        showConfirmButton: false
                    })
                    navigate("/addtocart")
                }
            } else {
                throw new Error('Failed to add to cart')
            }
        } catch (err) {
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: err.message || 'Failed to add to cart',
                confirmButtonText: 'OK'
            })
        }
    }

    return (
        <div>
            <section className="collection-wrapper">
                <div className="container">
                    <div className="row g-4">
                        <div className="col-lg-4">
                            <div className="product-slick">
                                <img 
                                    src={`uploads/${product.pimg}`} 
                                    alt={product.pname}
                                    className="w-100 img-fluid lazyload" 
                                    style={{ height: "500px", objectFit: 'contain' }} 
                                />
                            </div>
                        </div>
                        
                        <div className="col-lg-6">
                            <div className="product-page-details sticky-details mt-0">
                                <div className="trending-text">
                                    <h5>Available Quantity: {product.pq}</h5>
                                </div>

                                <h2 className="main-title">{product.pname}</h2>

                                <div className="price-text">
                                    <h3>
                                        <span className="fw-normal">Price:</span>
                                        â‚¹{product.pprice}
                                    </h3>
                                </div>

                                <div className="quantity-section my-4">
                                    <label htmlFor="quantity">Quantity:</label>
                                    <input
                                        type="number"
                                        id="quantity"
                                        min="1"
                                        max={product.pq}
                                        value={quantity}
                                        onChange={handleQuantityChange}
                                        className="form-control w-25 d-inline-block mx-3"
                                    />
                                </div>

                                <div className="accordion accordion-flush product-accordion">
                                    <div className="accordion-item">
                                        <h2 className="accordion-header">
                                            <button className="accordion-button collapsed" type="button"
                                                data-bs-toggle="collapse" data-bs-target="#product-description"
                                                aria-expanded="false">
                                                Product Description
                                            </button>
                                        </h2>
                                        <div id="product-description" className="accordion-collapse collapse">
                                            <div className="accordion-body">
                                                <p>{product.pdes}</p>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                <button 
                                    className="btn btn-solid mt-4" 
                                    onClick={addToCart}
                                    disabled={product.pq <= 0}
                                >
                                    {product.pq <= 0 ? 'Out of Stock' : 'Add to Cart'}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    )
}

export default Productdetails
