import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { useNavigate, useSearchParams } from 'react-router-dom'
import Swal from 'sweetalert2'

const Productdetails = () => {
    const [pname, setPname] = useState('');
    const [pq, setPq] = useState(1); // total quantity available
    const [pimg, setPimg] = useState('');
    const [pprice, setPprice] = useState(0);
    const [pdes, setPdes] = useState('');
    const [userQty, setUserQty] = useState(1); // quantity user selects to add

    const navigate = useNavigate();
    const [params] = useSearchParams();
    const idd = params.get("id");

    const { Loggedin, email: username } = useSelector((state) => state.userslice);

    useEffect(() => {
        details();
    }, []);

    const details = async () => {
        try {
            const res = await fetch(`http://localhost:9000/api/pdetails/${idd}`);
            if (res.ok) {
                const result = await res.json();
                if (result.statuscode === 1) {
                    setPimg(result.dd.pimg);
                    setPq(result.dd.pq);
                    setPname(result.dd.pname);
                    setPprice(result.dd.pprice);
                    setPdes(result.dd.pdescription);
                }
            }
        } catch (err) {
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: err.message || 'Failed to load product details',
                confirmButtonText: 'OK'
            });
        }
    };

    const addcart = async () => {
        if (!Loggedin) {
            await Swal.fire({
                icon: 'warning',
                title: 'Login Required',
                text: 'Please login to add items to your cart',
                confirmButtonText: 'OK'
            });
            navigate({
                pathname: '/login',
                search: `?id=${idd}`,
            });
            return;
        }

        try {
            const quantity = userQty > 0 ? userQty : 1;
            const tprice = pprice * quantity;
            const alldata = { pname, pq: quantity, pimg, pprice, pdes, username, tprice, idd };

            const checkRes = await fetch(`http://localhost:9000/api/checkcart?username=${username}&idd=${idd}`);
            if (!checkRes.ok) throw new Error(`Failed to check cart: HTTP ${checkRes.status}`);

            const result = await checkRes.json();
            if (!result || typeof result.statuscode !== 'number') throw new Error('Invalid server response');

            switch (result.statuscode) {
                case 2: {
                    // Product already in cart: update quantity
                    const updateRes = await fetch("http://localhost:9000/api/updatecart", {
                        method: "PUT",
                        headers: { 'Content-Type': 'application/json' },
                        body: JSON.stringify({
                            username,
                            productid: idd,
                            newQuantity: quantity
                        })
                    });
                    const updateResult = await updateRes.json();
                    if (updateResult.statuscode === 1) {
                        await Swal.fire({
                            icon: 'success',
                            title: 'Updated',
                            text: 'Cart quantity updated!',
                            timer: 2000,
                            showConfirmButton: false
                        });
                    } else {
                        throw new Error(updateResult.message || 'Failed to update quantity');
                    }
                    break;
                }
                case 3: {
                    // Product not in cart: add new item
                    const addRes = await fetch("http://localhost:9000/api/addcart", {
                        method: "POST",
                        headers: { 'Content-Type': 'application/json' },
                        body: JSON.stringify(alldata)
                    });
                    const addResult = await addRes.json();
                    if (addResult.statuscode === 1) {
                        await Swal.fire({
                            icon: 'success',
                            title: 'Added',
                            text: 'Product added to cart!',
                            timer: 2000,
                            showConfirmButton: false
                        });
                    } else {
                        throw new Error(addResult.message || 'Failed to add to cart');
                    }
                    break;
                }
                case 4:
                    throw new Error('Unexpected new user status for logged-in user');
                default:
                    throw new Error(`Unexpected status code: ${result.statuscode}`);
            }

            navigate("/addtocart");

        } catch (err) {
            console.error('Cart operation failed:', err);
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: err.message || 'Failed to process cart request',
                confirmButtonText: 'OK'
            });
        }
    };

    return (
        <div>
            <section>
                <div className="collection-wrapper">
                    <div className="container">
                        <div className="collection-wrapper">
                            <div className="row g-4">
                                <div className="col-lg-4">
                                    <div className="product-slick">
                                        <div>
                                            <img src={`uploads/${pimg}`} alt=""
                                                className="w-100 img-fluid lazyload" style={{ height: "500px" }} />
                                        </div>
                                    </div>
                                </div>
                                <div className="col-lg-4" style={{ marginLeft: "100px", height: "700px" }}>
                                    <div className="product-page-details product-description-box sticky-details mt-0">
                                        <div className="trending-text">
                                            <img src="../assets/images/product-details/trending.gif" className="img-fluid" alt="" />
                                            <h5>Available Quantity: {pq}</h5>
                                        </div>

                                        <h2 className="main-title">Product Name: {pname}</h2>

                                        <div className="price-text">
                                            <h3><span className="fw-normal">MRP:</span> â‚¹ {pprice}</h3>
                                            <span>Inclusive all taxes</span>
                                        </div>

                                        <div className="mb-3">
                                            <label htmlFor="quantity">Select Quantity:</label>
                                            <input
                                                type="number"
                                                id="quantity"
                                                min="1"
                                                max={pq}
                                                value={userQty}
                                                onChange={(e) => setUserQty(Number(e.target.value))}
                                                className="form-control"
                                            />
                                        </div>

                                        <div className="accordion accordion-flush product-accordion" id="accordionFlushExample">
                                            <div className="accordion-item">
                                                <h2 className="accordion-header">
                                                    <button className="accordion-button collapsed" type="button"
                                                        data-bs-toggle="collapse" data-bs-target="#flush-collapseOne">
                                                        Product Description
                                                    </button>
                                                </h2>
                                                <div id="flush-collapseOne" className="accordion-collapse collapse"
                                                    data-bs-parent="#accordionFlushExample">
                                                    <div className="accordion-body">
                                                        <p>{pdes}</p>
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="accordion-item">
                                                <h2 className="accordion-header">
                                                    <button className="accordion-button" type="button" data-bs-toggle="collapse"
                                                        data-bs-target="#flush-collapseTwo">
                                                        Information
                                                    </button>
                                                </h2>
                                                <div id="flush-collapseTwo" className="accordion-collapse collapse show"
                                                    data-bs-parent="#accordionFlushExample">
                                                    <div className="accordion-body">
                                                        <ul className="shipping-info">
                                                            <li><span>SKU: </span>SP18 (COPY)</li>
                                                            <li><span>Unit: </span>1 Item</li>
                                                            <li><span>Weight: </span>150 Gms</li>
                                                            <li><span>Stock Status: </span>In stock</li>
                                                            <li><span>Quantity: </span>{pq} Items Left</li>
                                                        </ul>
                                                        <button
                                                            className="btn btn-solid"
                                                            style={{ marginTop: "30px" }}
                                                            onClick={addcart}
                                                        >
                                                            Add to Cart
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    );
};

export default Productdetails;
