import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { useNavigate, useSearchParams } from 'react-router-dom'

const Productdetails = () => {
   
    const[pname,setpname]=useState()
    const[pq,setpq]=useState()
    const[pimg,setpimg]=useState()
    const[pprice,setpprice]=useState()
    const[pdes,setpdes]=useState()
    const navigate=useNavigate()
    const[params]= useSearchParams()
    const id=params.get("id")

    const{Loggedin,username}=useSelector((state)=>{
        return state.userslice
    })
    useEffect(()=>{
        details()
    },[])
    const details=async()=>{
        try{
            const res= await fetch(`http://localhost:9000/api/pdetails/${id}`,{
                method:"get"
            })
            if(res.ok){
                const result=await res.json()
                if(result.statuscode===1){
                    setpimg(result.dd.pimg)
                    setpq(result.dd.pq)
                    setpname(result.dd.pname)
                    setpprice(result.dd.pprice)
                    setpdes(result.dd.pdescription)

                }
            }
        }catch(err){
            alert(err)
        }

    }
    const addcart=async()=>{
        const uname=username
        const tprice=pprice*pq
        const alldata={pname,pq,pimg,pprice,pdes,uname,tprice,id}
        // const formdata=new FormData()
        // formdata.append("pname",pname)
        // formdata.append("pq",pq)
        // formdata.append("pimg",pimg)
        // formdata.append("pprice",pprice)
        // formdata.append("pdes",pdes)
        // formdata.append("uname",uname)
        // formdata.append("tprice",tprice)
        // formdata.append("id",id)



        if(Loggedin){
            try{
                const res= await fetch(`http://localhost:9000/api/checkcart?username=${uname}&& idd=${id}`)
                if(res.ok){
                    const result= await res.json()
                    if(result.statuscode===3){
                        try{
                           
                            const res=await fetch("http://localhost:9000/api/addcart",{
                                method:"post",
                                body:JSON.stringify(alldata),
                                headers:{
                                    'Content-type':'application/json; charset=UTF-8'
                                }
                            })
                            if(res.ok){
                                const result=await res.json()
                                if(result.statuscode===1){
                                    alert(P)
                                    navigate("/addtocart")
                                   


                                }
                                else{
                                    alert("data didn't add to cart")
                                }
                            }

                        }catch(err){
                            alert(err)
                        }
                    }else{
                        alert("Quantity has to be added")
                    }
                }

            }catch(err){
                alert(err)
            }
        }
        
    }
  return (
    <div>
        <section>
        <div class="collection-wrapper">
            <div class="container">
                <div class="collection-wrapper">
                    <div class="row g-4">
                        <div class="col-lg-4">
                
                             
                                    
                            
                            <div class="product-slick">
                      
                            
                           
                               

                                 <div>
                                <img src={`uploads/${pimg}`} alt=""
                                        class="w-100 img-fluid lazyload" style={{height:"500px"}}/></div> 
                   
                                        
                                   
                            </div>
                            
                            <div class="row">
                                <div class="col-12">
                                    <div class="slider-nav">
                                      
                                    </div>
                                </div>
                            
                            </div>
                           
                          
                            
            
                        </div>
                        <div class="col-lg-4" style={{marginLeft:"100px",height:"700px"}}>
                            <div class="product-page-details product-description-box sticky-details mt-0">

                                <div class="trending-text ">
                                    <img src="../assets/images/product-details/trending.gif" class="img-fluid" alt=""/>
                                    <h5>Product Quantity:{pq}
                                    </h5>
                                </div>

                                <h2 class="main-title"> Product Name:{pname} </h2>
                              

                                <div class="price-text">
                                    <h3><span class="fw-normal">MRP:</span>
                                    â‚¹ {pprice}
                                    </h3><span>Inclusive all the text </span>
                                </div>

                              


                                <div class="accordion accordion-flush product-accordion" id="accordionFlushExample">
                                    <div class="accordion-item">
                                        <h2 class="accordion-header">
                                            <button class="accordion-button collapsed" type="button"
                                                data-bs-toggle="collapse" data-bs-target="#flush-collapseOne"
                                                aria-expanded="false" aria-controls="flush-collapseOne">
                                                Product Description </button>
                                        </h2>
                                        <div id="flush-collapseOne" class="accordion-collapse collapse"
                                            data-bs-parent="#accordionFlushExample">
                                            <div class="accordion-body">
                                                <p>{pdes}</p>
                                            </div>
                                        </div>
                                    </div>
                                    <div class="accordion-item">
                                        <h2 class="accordion-header">
                                            <button class="accordion-button" type="button" data-bs-toggle="collapse"
                                                data-bs-target="#flush-collapseTwo" aria-expanded="false"
                                                aria-controls="flush-collapseTwo">
                                                Information </button>
                                        </h2>
                                        <div id="flush-collapseTwo" class="accordion-collapse collapse show"
                                            data-bs-parent="#accordionFlushExample">
                                            <div class="accordion-body">
                                                <div class="bordered-box border-0 mt-0 pt-0">
                                                    <h4 class="sub-title">
                                                        product Info</h4>
                                                    <ul class="shipping-info">
                                                        <li><span>SKU: </span>SP18
                                                            (COPY) </li>

                                                        <li><span>Unit: </span>1
                                                            Item </li>

                                                        <li><span>Weight:
                                                            </span>150 Gms </li>

                                                        <li><span>Stock Status:
                                                            </span>In stock </li>

                                                        <li><span>Quantity:
                                                            </span>40 Items Left </li>
                                                    </ul>
                                                </div>
                                                <button class="btn btn-solid" style={{marginTop:"100px",marginLeft:"-90px"}} onClick={addcart}>Add to cart</button>
                                               

                                              
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                   
                        
                       
                    </div>
               
                </div>
             
            </div>
        </div>
    </section>

      
    </div>
  )
}

export default Productdetails
