import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useNavigate, useSearchParams } from 'react-router-dom';
import Swal from 'sweetalert2';

const Productdetails = () => {
  const [product, setProduct] = useState({});
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const [params] = useSearchParams();
  const productId = params.get("id");

  const { Loggedin, email } = useSelector((state) => state.userslice);

  useEffect(() => {
    fetchProductDetails();
  }, []);

  const fetchProductDetails = async () => {
    try {
      const res = await fetch(`http://localhost:9000/api/pdetails/${productId}`);
      if (!res.ok) throw new Error("Failed to fetch product details");

      const result = await res.json();
      if (result.statuscode === 1) {
        setProduct(result.dd);
      } else {
        throw new Error("Invalid product data");
      }
    } catch (err) {
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: err.message || 'Failed to load product details',
        confirmButtonText: 'OK',
      });
    } finally {
      setLoading(false);
    }
  };

  const addToCart = async () => {
    if (!Loggedin) {
      await Swal.fire({
        icon: 'warning',
        title: 'Login Required',
        text: 'Please login to add items to your cart',
        confirmButtonText: 'OK',
      });
      navigate({
        pathname: '/login',
        search: `?pid=${productId}`,
      });
      return;
    }

    try {
      const quantity = Number(product.pq) || 1;
      const tprice = product.pprice * quantity;

      const cartData = {
        pname: product.pname,
        pq: quantity,
        pimg: product.pimg,
        pprice: product.pprice,
        pdes: product.pdescription,
        emaill: email,
        tprice,
        productid: productId,
      };

      const checkRes = await fetch(`http://localhost:9000/api/checkcart?username=${email}&idd=${productId}`);
      const checkResult = await checkRes.json();

      if (checkResult.statuscode === 2) {
        // Product exists in cart - update
        const updateRes = await fetch("http://localhost:9000/api/updatecart", {
          method: "PUT",
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            username: email,
            productid: productId,
            newQuantity: quantity,
            pprice: product.pprice,
          }),
        });

        const updateResult = await updateRes.json();
        if (updateResult.statuscode === 1) {
          await Swal.fire({
            icon: 'success',
            title: 'Updated',
            text: 'Product quantity updated in cart.',
            timer: 2000,
            showConfirmButton: false,
          });
        } else {
          throw new Error(updateResult.message || 'Failed to update cart');
        }
      } else if (checkResult.statuscode === 3) {
        // Product not in cart - add new
        const addRes = await fetch("http://localhost:9000/api/addcart", {
          method: "POST",
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(cartData),
        });

        const addResult = await addRes.json();
        if (addResult.statuscode === 1) {
          await Swal.fire({
            icon: 'success',
            title: 'Added',
            text: 'Product added to cart!',
            timer: 2000,
            showConfirmButton: false,
          });
        } else {
          throw new Error(addResult.message || 'Failed to add product to cart');
        }
      } else {
        throw new Error('Unexpected status code');
      }

      navigate("/addtocart");
    } catch (err) {
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: err.message || 'Failed to add to cart',
        confirmButtonText: 'OK',
      });
    }
  };

  if (loading) return <div className="text-center mt-5">Loading product details...</div>;

  return (
    <div className="collection-wrapper">
      <div className="container">
        <div className="row g-4">
          <div className="col-lg-4">
            <div className="product-slick">
              <img src={`uploads/${product.pimg}`} alt="" className="w-100 img-fluid" style={{ height: "500px" }} />
            </div>
          </div>

          <div className="col-lg-6">
            <div className="product-page-details mt-0">
              <div className="trending-text mb-3">
                <img src="../assets/images/product-details/trending.gif" className="img-fluid" alt="" />
                <h5>Product Quantity: {product.pq}</h5>
              </div>

              <h2 className="main-title">Product Name: {product.pname}</h2>

              <div className="price-text my-3">
                <h3><span className="fw-normal">MRP:</span> â‚¹ {product.pprice}</h3>
                <span>Inclusive of all taxes</span>
              </div>

              <div className="accordion accordion-flush product-accordion" id="accordionFlushExample">
                <div className="accordion-item">
                  <h2 className="accordion-header">
                    <button className="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#desc" aria-expanded="false" aria-controls="desc">
                      Product Description
                    </button>
                  </h2>
                  <div id="desc" className="accordion-collapse collapse" data-bs-parent="#accordionFlushExample">
                    <div className="accordion-body">
                      <p>{product.pdescription}</p>
                    </div>
                  </div>
                </div>

                <div className="accordion-item">
                  <h2 className="accordion-header">
                    <button className="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#info" aria-expanded="true" aria-controls="info">
                      Information
                    </button>
                  </h2>
                  <div id="info" className="accordion-collapse collapse show" data-bs-parent="#accordionFlushExample">
                    <div className="accordion-body">
                      <ul className="shipping-info">
                        <li><span>SKU: </span>SP18 (COPY)</li>
                        <li><span>Unit: </span>1 Item</li>
                        <li><span>Weight: </span>150 Gms</li>
                        <li><span>Stock Status: </span>In stock</li>
                        <li><span>Quantity Left: </span>{product.pq}</li>
                      </ul>
                      <button className="btn btn-solid mt-4" onClick={addToCart}>Add to Cart</button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div >
      </div >
    </div >
  );
};

export default Productdetails;
