import { createSlice } from "@reduxjs/toolkit";

const state = {
  Loggedin: false,
  username: "Guest",
  usertype: "",
  firstname: "",
  email: "",
  userId: "", // ✅ Add userId
};

const userslice = createSlice({
  name: "userslice",
  initialState: state,
  reducers: {
    Login: (state, action) => {
      state.Loggedin = true;
      state.firstname = action.payload.fname;
      state.username = action.payload.pname;
      state.usertype = action.payload.utype;
      state.email = action.payload.email;
      state.userId = action.payload.userId; // ✅ Set userId from payload
    },
    Logout: (state) => {
      state.Loggedin = false;
      state.firstname = "";
      state.username = "Guest";
      state.usertype = "";
      state.email = "";
      state.userId = ""; // ✅ Clear userId on logout
    },
  },
});

export const { Login, Logout } = userslice.actions;
export default userslice.reducer;
