import { useEffect, useState } from "react";

export const Userdata = () => {


    const [user, setuser] = useState([])

    useEffect(() => {

        getuser()
    }, [])




    const getuser = async () => {

        const res = await fetch("http://localhost:9000/api/alluser", {

            method: "GET"

        });
        if (res.ok) {
            const result = await res.json()
            if (result.statuscode === 1) {
                setuser(result.userlist)

            }
            else {
                alert("No data Fetched")
            }

        }



    }

    const del= async(id)=>{

        const res= await fetch(`http://localhost:9000/api/alluser/${id}`,{
            method:"DELETE"
        });
        if(res.ok){
            const result=await res.json();
            if(result.statuscode===1){
                alert("User deleted")
            }
            else{
                
            }
        }


    }

    return (


        <>

            <h1>ALL user</h1>

            <table className="table table-bordered table-striped mt-3">
                <tr>
                    <td>Userid</td>
                    <td>FirstName</td>
                    <td>lastname</td>
                    <td>Email</td>
                    <td>Usertype</td>
                    <td>Change userType</td>
                    <td>Delete user</td>
                </tr>
                {
                    user.map((data, i) => (

                        <tr key={i}>
                             <td>{data._id}</td>
                            <td>{data.firstname}</td>
                            <td>{data.lastname}</td>
                            <td>{data.email}</td>
                            <td>{data.usertype}</td>
                            <td><button className="btn btn-solid w-auto">Admin</button><button className="btn btn-solid w-auto">User</button></td>
                            <td><button className="btn btn-solid w-auto" onClick={()=>(del(data._id))}>Delete</button></td>


                        </tr>


                    ))
                }


            </table>
        </>
    )
}
