import { useEffect, useState } from "react";
import { Routes } from "react-router-dom";
import Header from "./header";
import SiteRoutes from "./routes";
import Userheader from "./userheader";
import context from "./usecontext";
import { Footer } from "./footer";
import { Landing } from "./landingpage";

function App() {
  const [usertype, setUsertype] = useState(null);
  const [flag, setFlag] = useState(null);
  const [orderid, setOrderid] = useState(null);

  useEffect(() => {
    const userData = sessionStorage.getItem("userinfo");
    if (userData) {
      const user = JSON.parse(userData);
      setUsertype(user.usertype); // usertype: 'admin' or 'user'
    } else {
      setUsertype(null);
    }
  }, [flag]); // Re-run if flag changes, for flexibility (e.g. setFlag from login)

  return (
    <context.Provider value={{ flag, setFlag, orderid, setOrderid }}>
      {usertype === "admin" ? <Header /> : <Userheader />}

      <SiteRoutes />
      <Footer />
    </context.Provider>
  );
}

export default App;
