import React, { useContext, useEffect, useState } from 'react';
import context from './usecontext';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { useSelector } from 'react-redux';

const Addtocart = () => {
  const [cartdata, setcartdata] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { orderid, setorderid } = useContext(context);
  const [tprice, settprice] = useState(0);
  const navigate = useNavigate();

  const { Loggedin, userId } = useSelector((state) => state.userslice);

  useEffect(() => {
  const token = sessionStorage.getItem('token');
  
  if (!token) {
    navigate('/login');
    return;
  }

  if (!Loggedin || !userId) {
    // Instead of immediate redirect, try to validate token
    validateToken(token);
    return;
  }

  addcart();
}, [Loggedin, userId]);

const validateToken = async (token) => {
  try {
    const res = await fetch('http://localhost:9000/api/validate-token', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    
    if (res.ok) {
      // Token is valid, but Redux state might not be updated
      // You might need to dispatch login action here
    } else {
      navigate('/login');
    }
  } catch (err) {
    navigate('/login');
  }
};

  const grandtotal = (cart) => {
    const total = cart.reduce((acc, item) => acc + item.productprice * item.qnt, 0);
    settprice(total);
    sessionStorage.setItem('billamt', JSON.stringify(total));
  };

  const addcart = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`http://localhost:9000/api/cartdata/${userId}`, {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${sessionStorage.getItem('token')}`,
        },
      });

      if (!res.ok) {
        throw new Error('Failed to fetch cart data');
      }

      const result = await res.json();
      if (result.statuscode === 1) {
        const updated = result.data.map((item) => ({
          ...item,
          qnt: item.pq || 1,
        }));
        setcartdata(updated);
        grandtotal(updated);
      } else {
        setcartdata([]);
        settprice(0);
      }
    } catch (err) {
      console.error('Cart error:', err);
      setError(err.message);
      Swal.fire('Error', 'Failed to load your cart', 'error');
    } finally {
      setLoading(false);
    }
  };

  const del = async (id) => {
    const result = await Swal.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#ec8951',
      cancelButtonColor: '#212121',
      confirmButtonText: 'Yes, delete it!',
    });

    if (!result.isConfirmed) return;

    try {
      const res = await fetch(`http://localhost:9000/api/cartdelete/${id}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${sessionStorage.getItem('token')}`,
        },
      });

      if (res.ok) {
        const result = await res.json();
        if (result.statuscode === 1) {
          Swal.fire('Deleted!', 'Your product has been deleted.', 'success');
          addcart();
        }
      }
    } catch (err) {
      Swal.fire('Error', err.message, 'error');
    }
  };

  const checkout = () => {
    if (cartdata.length === 0) {
      Swal.fire('Your cart is empty!', 'Please add some products before checkout.', 'warning');
      return;
    }

    const orderId = Math.floor(Math.random() * 9000000) + 1000000;
    setorderid(orderId);
    navigate('/checkout');
  };

  // ... (keep your existing updateCartQuantity, increament1, decreament1 functions)

  const CartTable = () => (
    <table className="table cart-table">
      <thead>
        <tr className="table-head">
          <th>image</th>
          <th>product name</th>
          <th>price</th>
          <th>quantity</th>
          <th>total</th>
          <th>action</th>
        </tr>
      </thead>
      <tbody>
        {cartdata.map((dd, i) => (
          <tr key={dd._id}>
            <td>
              <img src={`uploads/${dd.productimage}`} className="img-fluid" alt={dd.productname} />
            </td>
            <td>
              <a href="">{dd.productname}</a>
            </td>
            <td className="table-price">
              <h2>₹{dd.productprice}</h2>
            </td>
            <td>
              <div className="qty-box">
                <div className="input-group qty-container">
                  <button className="btn qty-btn-minus" >
                    <i className="ri-arrow-left-s-line"></i>
                  </button>
                  <input
                    type="number"
                    readOnly
                    name="qty"
                    className="form-control input-qty"
                    value={dd.qnt}
                  />
                  <button className="btn qty-btn-plus" >
                    <i className="ri-arrow-right-s-line"></i>
                  </button>
                </div>
              </div>
            </td>
            <td>
              <h2 className="td-color">₹{dd.productprice * dd.qnt}</h2>
            </td>
            <td>
              <a className="icon remove-btn" onClick={() => del(dd._id)}>
                <i className="ri-close-line"></i>
              </a>
            </td>
          </tr>
        ))}
      </tbody>
      <tfoot>
        <tr>
          <td colSpan="4" className="d-md-table-cell d-none">
            total price :
          </td>
          <td className="d-md-none">total price :</td>
          <td>
            <h2>₹{tprice}</h2>
          </td>
        </tr>
      </tfoot>
    </table>
  );

  const EmptyCart = () => (
    <div className="empty-cart text-center py-5">
      <i className="ri-shopping-cart-line" style={{ fontSize: '5rem', color: '#ccc' }}></i>
      <h3 className="mt-3">Your cart is empty</h3>
      <p className="mb-4">Looks like you haven't added anything to your cart yet</p>
      <a href="/products" className="btn btn-solid">
        Continue Shopping
      </a>
    </div>
  );

  if (loading) {
    return (
      <div className="container text-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p className="mt-2">Loading your cart...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container text-center py-5">
        <div className="alert alert-danger">{error}</div>
        <button className="btn btn-solid mt-3" onClick={addcart}>
          Retry
        </button>
      </div>
    );
  }

  return (
    <div>
      <section className="cart-section section-b-space">
        <div className="container">
          {cartdata.length > 0 ? (
            <>
              <div className="cart_counter">
                <div className="countdownholder">
                  Your cart will be expired in <span id="timer"></span> minutes!
                </div>
                <a href="" className="cart_checkout btn btn-solid btn-xs">
                  check out
                </a>
              </div>

              <div className="table-responsive">
                <CartTable />
              </div>

              <div className="row cart-buttons">
                <div className="col-3">
                  <a href="/products" className="btn btn-solid text-capitalize">
                    continue shopping
                  </a>
                </div>
                <div className="col-6"></div>
                <div className="col-3">
                  <button className="btn btn-solid text-capitalize" onClick={checkout}>
                    check out
                  </button>
                </div>
              </div>
            </>
          ) : (
            <EmptyCart />
          )}
        </div>
      </section>
    </div>
  );
};

export default Addtocart;
