import React, { useContext, useEffect, useState } from 'react';
import context from './usecontext';
import { Link, useLocation, useNavigate, useParams, useSearchParams } from 'react-router-dom';
import { useDispatch, useSelector , } from 'react-redux';
import Swal from 'sweetalert2';
import { Login } from './reducer/userslice';

const Loginp = () => {
    const location = useLocation();
    const { fname } = location.state || {};
    const [email, setemail] = useState('');
    const [pwd, setpwd] = useState('');
    const navigate = useNavigate();
    const [Param]=useSearchParams()

    const { setflag } = useContext(context);
    const { Loggedin } = useSelector((state) => state.userslice);
    const tokenid= Param.get("token")
    const id= Param.get("idd")
    
const dispatch=useDispatch();
   
    const validation = () => {
        const errors = {};

        if (!email || !/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(email)) {
            errors.email = "Please enter a valid email address.";
        }

      

        if (Object.keys(errors).length !== 0) {
            Swal.fire({
                icon: 'error',
                title: 'Validation Error',
                html: `<ul style="text-align: left;">
                        ${Object.values(errors).map(err => `<li>${err}</li>`).join('')}
                       </ul>`
            });
            return false;
        }

        return true;
    };


 
    const show = async () => {
        if (!validation()) return;

        const data1 = { email, pwd };

        try {
            const response1 = await fetch("http://localhost:9000/api/login", {
                method: "post",
                body: JSON.stringify(data1),
                headers: { 'Content-type': 'application/json;charset=utf-8' },

                
            });
           

            if (response1.ok) {
                const result1 = await response1.json();

                if (result1.statuscode === 1) {
                    await Swal.fire({
                        icon: 'success',
                        title: 'Login Successful',
                        text: 'You have successfully logged in!',
                        timer: 2000,
                        showConfirmButton: false
                    });
                    if (id) {
								navigate({
									pathname: '/productdetails',
									search: `?id=${id}`,
								});
                                dispatch(Login(result1.userdata))
								sessionStorage.setItem("userinfo", JSON.stringify(result1.userdata))
								sessionStorage.setItem("token", JSON.stringify(result1.authtoken))
                            }

                 else  if (result1.usertype === "admin") {
								navigate("/home")
								dispatch(Login(result1.userdata))
								sessionStorage.setItem("userinfo", JSON.stringify(result1.userdata))
								sessionStorage.setItem("token", JSON.stringify(result1.authtoken))
							}
                else if (result1.usertype === "user") {
								navigate("/")
								dispatch(Login(result1.userdata))
								sessionStorage.setItem("userinfo", JSON.stringify(result1.userdata))
								sessionStorage.setItem("token", JSON.stringify(result1.authtoken))
							}
                          
                

                } else {
                    Swal.fire({
                        icon: 'error',
                        title: 'Registration Required',
                        text: 'Please register first before logging in',
                        confirmButtonText: 'OK'
                    });
                }
            }
        } catch (err) {
            Swal.fire({
                icon: 'error',
                title: 'Login Failed',
                text: 'An error occurred during login. Please try again.',
                confirmButtonText: 'OK'
            });
            console.error(err);
        }
    };

    return (
        <div>
            <section className="login-page section-b-space">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-6">
                            <h3>Login</h3>
                            <div className="theme-card">
                                <div className="theme-form">
                                    <div className="form-box">
                                        <label htmlFor="email" className="form-label">Email</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            id="email"
                                            placeholder="Email"
                                            onChange={(e) => setemail(e.target.value)}
                                        />
                                    </div>
                                    <div className="form-box">
                                        <label htmlFor="review" className="form-label">Password</label>
                                        <input
                                            type="password"
                                            className="form-control"
                                            id="review"
                                            placeholder="Enter your password"
                                            onChange={(e) => setpwd(e.target.value)}
                                        />
                                    </div>
                                    <button className="btn btn-solid" onClick={show}>Login</button>
                                </div>
                            </div>
                        </div>
                        <div className="col-lg-6 right-login">
                            <h3>New Customer</h3>
                            <div className="theme-card authentication-right">
                                <h6 className="title-font">Create An Account</h6>
                                <p>
                                    Sign up for a free account at our store. Registration is quick and easy.
                                    To start shopping click register.
                                </p>
                                <Link to="/register" className="btn btn-solid">Create an Account</Link>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    );
};

export default Loginp;
