import React, { useContext, useEffect, useState } from 'react';
import context from './usecontext';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { useSelector } from "react-redux";

const Addtocart = () => {
  const [cartdata, setCartdata] = useState([]);
  const { orderid, setorderid } = useContext(context);
  const [tprice, setTprice] = useState(0);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { Loggedin, userid } = useSelector((state) => state.userslice);

  useEffect(() => { 
    if (Loggedin) {
      fetchCartItems();
    } else {
      navigate('/login');
    }
  }, [Loggedin, userid]);

  const fetchCartItems = async () => {
    try {
      setLoading(true);
      const res = await fetch(`http://localhost:9000/api/cartdata/${userid}`);
      
      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }

      const result = await res.json();
      
      if (result.statuscode === 1 && result.data) {
        // Ensure each item has a quantity field
        const itemsWithQuantity = result.data.map(item => ({
          ...item,
          qnt: item.productquantity || 1
        }));
        setCartdata(itemsWithQuantity);
      } else {
        setCartdata([]);
      }
    } catch (err) {
      console.error("Error fetching cart items:", err);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'Failed to load cart items. Please try again.',
        confirmButtonText: 'OK',
      });
    } finally {
      setLoading(false);
    }
  };

  // Calculate total price whenever cartdata changes
  useEffect(() => {
    const total = cartdata.reduce((acc, item) => acc + (parseFloat(item.productprice) * parseInt(item.qnt)), 0);
    setTprice(total.toFixed(2));
    sessionStorage.setItem("billamt", JSON.stringify(total));
  }, [cartdata]);

  const updateQuantity = async (index, newQuantity) => {
    if (newQuantity < 1) return;

    const updatedCart = [...cartdata];
    updatedCart[index].qnt = newQuantity;
    setCartdata(updatedCart);

    try {
      const res = await fetch(`http://localhost:9000/api/updatecart`, {
        method: "PUT",
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${sessionStorage.getItem('token')}`
        },
        body: JSON.stringify({
          userid: userid,
          productid: updatedCart[index].productid,
          newQuantity: newQuantity,
          productprice: updatedCart[index].productprice
        })
      });

      if (!res.ok) {
        throw new Error('Failed to update cart quantity');
      }
    } catch (err) {
      console.error('Error updating cart:', err);
      // Revert to previous data if update fails
      fetchCartItems();
    }
  };

  const incrementQuantity = (index) => {
    const newQuantity = cartdata[index].qnt + 1;
    updateQuantity(index, newQuantity);
  };

  const decrementQuantity = (index) => {
    if (cartdata[index].qnt > 1) {
      const newQuantity = cartdata[index].qnt - 1;
      updateQuantity(index, newQuantity);
    }
  };

  const deleteCartItem = async (id) => {
    const result = await Swal.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#ec8951',
      cancelButtonColor: '#212121',
      confirmButtonText: 'Yes, delete it!'
    });
    
    if (!result.isConfirmed) return;

    try {
      const res = await fetch(`http://localhost:9000/api/cartdelete/${id}`, {
        method: "DELETE",
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${sessionStorage.getItem('token')}`
        }
      });
      
      if (res.ok) {
        const result = await res.json();
        if (result.statuscode === 1) {
          Swal.fire('Deleted!', 'Your product has been deleted.', 'success');
          fetchCartItems();
        }
      } else {
        throw new Error('Failed to delete item');
      }
    } catch (err) {
      Swal.fire('Error', err.message || 'Failed to delete item', 'error');
    }
  };

  const checkout = () => {
    if (cartdata.length === 0) {
      Swal.fire('Your cart is empty!', 'Please add some products before checkout.', 'warning');
      return;
    }
    
    const r = Math.floor(Math.random() * 9000000) + 1000000;
    setorderid(r);
    navigate("/checkout");
  };

  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p>Loading your cart...</p>
      </div>
    );
  }

  return (
    <div>
      <section className="cart-section section-b-space">
        <div className="container">
          <div className="cart_counter">
            <div className="countdownholder">
              Your cart will be expired in<span id="timer"></span> minutes!
            </div>
            {cartdata.length > 0 && (
              <button className="cart_checkout btn btn-solid btn-xs" onClick={checkout}>check out</button>
            )}
          </div> 
          <div className="table-responsive">
            <table className="table cart-table">
              <thead>
                <tr className="table-head">
                  <th>image</th>
                  <th>product name</th>
                  <th>price</th>
                  <th>quantity</th>
                  <th>total</th>
                  <th>action</th>
                </tr>
              </thead>
              <tbody>
                {cartdata.length > 0 ? (
                  cartdata.map((item, index) => (
                    <tr key={item._id}>
                      <td>
                        <a href={`/product/${item.productid}`}>
                          <img 
                            src={`http://localhost:9000/uploads/${item.productimage}`} 
                            className="img-fluid" 
                            alt={item.productname}
                            style={{ width: '80px', height: '80px', objectFit: 'contain' }}
                          />
                        </a>
                      </td>
                      <td>
                        <a href={`/product/${item.productid}`}>{item.productname}</a>
                        <div className="mobile-cart-content row">
                          <div className="col">
                            <div className="qty-box">
                              <div className="input-group qty-container">
                                <button 
                                  className="btn qty-btn-minus" 
                                  onClick={() => decrementQuantity(index)}
                                >
                                  <i className="ri-arrow-left-s-line"></i>
                                </button>
                                <input 
                                  type="number" 
                                  readOnly 
                                  className="form-control input-qty" 
                                  value={item.qnt} 
                                />
                                <button 
                                  className="btn qty-btn-plus" 
                                  onClick={() => incrementQuantity(index)}
                                >
                                  <i className="ri-arrow-right-s-line"></i>
                                </button>
                              </div>
                            </div>
                          </div>
                          <div className="col table-price">
                            <h2 className="td-color">₹{item.productprice}</h2>
                          </div>
                          <div className="col">
                            <h2 className="td-color">
                              <button 
                                className="icon remove-btn" 
                                onClick={() => deleteCartItem(item._id)}
                                style={{ background: 'none', border: 'none' }}
                              >
                                <i className="ri-close-line"></i>
                              </button>
                            </h2>
                          </div>
                        </div>
                      </td>
                      <td className="table-price">
                        <h2>₹{item.productprice}</h2>
                      </td>
                      <td>
                        <div className="qty-box">
                          <div className="input-group qty-container">
                            <button 
                              className="btn qty-btn-minus" 
                              onClick={() => decrementQuantity(index)}
                            >
                              <i className="ri-arrow-left-s-line"></i>
                            </button>
                            <input 
                              type="number" 
                              readOnly 
                              className="form-control input-qty" 
                              value={item.qnt} 
                            />
                            <button 
                              className="btn qty-btn-plus" 
                              onClick={() => incrementQuantity(index)}
                            >
                              <i className="ri-arrow-right-s-line"></i>
                            </button>
                          </div>
                        </div>
                      </td>
                      <td>
                        <h2 className="td-color">₹{(item.productprice * item.qnt).toFixed(2)}</h2>
                      </td>
                      <td>
                        <button 
                          className="icon remove-btn" 
                          onClick={() => deleteCartItem(item._id)}
                          style={{ background: 'none', border: 'none' }}
                        >
                          <i className="ri-close-line"></i>
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="6" className="text-center py-5">
                      <div className="empty-cart-message">
                        <img 
                          src="/assets/images/empty-cart.png" 
                          alt="Empty Cart" 
                          style={{ width: '200px', marginBottom: '20px' }}
                        />
                        <h4>Your cart is empty</h4>
                        <p>Looks like you haven't added any items to your cart yet</p>
                        <a href="/products" className="btn btn-solid mt-3">Continue Shopping</a>
                      </div>
                    </td>
                  </tr>
                )}
              </tbody>
              {cartdata.length > 0 && (
                <tfoot>
                  <tr>
                    <td colSpan="4" className="d-md-table-cell d-none">total price :</td>
                    <td className="d-md-none">total price :</td>
                    <td>
                      <h2>₹{tprice}</h2>
                    </td>
                  </tr>
                </tfoot>
              )}
            </table>
          </div>

          {cartdata.length > 0 && (
            <div className="row cart-buttons mt-4">
              <div className="col-12 col-md-3">
                <a href="/products" className="btn btn-solid text-capitalize">continue shopping</a>
              </div>
              <div className="col-12 col-md-6"></div>
              <div className="col-12 col-md-3 text-md-end mt-3 mt-md-0">
                <button className="btn btn-solid text-capitalize" onClick={checkout}>proceed to checkout</button>
              </div>
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Addtocart;
