import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { useNavigate, useSearchParams } from 'react-router-dom'
import Swal from 'sweetalert2'

const Productdetails = () => {
    const [pname, setpname] = useState()
    const [pq, setpq] = useState()
    const [pimg, setpimg] = useState()
    const [pprice, setpprice] = useState()
    const [pdes, setpdes] = useState()
    const navigate = useNavigate()
    const [params] = useSearchParams()
    const idd = params.get("id")
    

    const { Loggedin, email } = useSelector((state) => {
        return state.userslice
    })



    useEffect(() => {
        details()
    }, [])

    const details = async () => {
        try {
            const res = await fetch(`http://localhost:9000/api/pdetails/${idd}`, {
                method: "get"
            })
            if (res.ok) {
                const result = await res.json()
                if (result.statuscode === 1) {
                    setpimg(result.dd.pimg)
                    setpq(result.dd.pq)
                    setpname(result.dd.pname)
                    setpprice(result.dd.pprice)
                    setpdes(result.dd.pdescription)
                }
            }
        } catch (err) {
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: err.message || 'Failed to load product details',
                confirmButtonText: 'OK'
            })
        }
    }

  const addcart = async () => {
  if (!Loggedin) {
    await Swal.fire({
      icon: 'warning',
      title: 'Login Required',
      text: 'Please login to add items to your cart',
      confirmButtonText: 'OK'
    });
    navigate({
      pathname: '/login',
      search: `?pid=${idd}`,
    });
    return;
  }

  try {
    const emaill = email;
    const tprice = pprice * pq;
    const alldata = { pname, pq, pimg, pprice, pdes, emaill, tprice, idd };

    // First check if product exists in cart
    const checkRes = await fetch(`http://localhost:9000/api/checkcart?username=${emaill}&idd=${idd}`);
    
    if (!checkRes.ok) {
      throw new Error(`HTTP error! status: ${checkRes.status}`);
    }

    const result = await checkRes.json();
    
    if (result.statuscode === 3) {
      // Product not in cart - add it
      const addRes = await fetch("http://localhost:9000/api/addcart", {
        method: "POST",
        body: JSON.stringify(alldata),
        headers: { 'Content-Type': 'application/json' }
      });

      if (!addRes.ok) {
        throw new Error(`Failed to add to cart. Status: ${addRes.status}`);
      }

      const addResult = await addRes.json();
      
      if (addResult.statuscode === 1) {
        await Swal.fire({
          icon: 'success',
          title: 'Success',
          text: 'Product added to cart!',
          timer: 2000,
          showConfirmButton: false
        });
        navigate("/addtocart");
      } else {
        throw new Error(addResult.message || "Server rejected the request");
      }
    } else if (result.statuscode === 2) {
      // Product already in cart - update quantity
      const updateRes = await fetch("http://localhost:9000/api/updatecart", {
        method: "PUT",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username: emaill,
          productid: idd,
          newQuantity: pq // Or pq + existing quantity if you want to add
        })
      });

      if (!updateRes.ok) {
        throw new Error(`Failed to update quantity. Status: ${updateRes.status}`);
      }

      const updateResult = await updateRes.json();
      
      if (updateResult.statuscode === 1) {
        await Swal.fire({
          icon: 'success',
          title: 'Updated',
          text: 'Product quantity updated in cart!',
          timer: 2000,
          showConfirmButton: false
        });
        navigate("/addtocart");
      } else {
        throw new Error(updateResult.message || "Failed to update quantity");
      }
    } else {
      throw new Error("Unexpected response from server");
    }
  } catch (err) {
    console.error('Cart error:', err);
    Swal.fire({
      icon: 'error',
      title: 'Error',
      text: err.message || 'Failed to process cart request',
      confirmButtonText: 'OK'
    });
  }
};


    return (
        <div>
            <section>
                <div className="collection-wrapper">
                    <div className="container">
                        <div className="collection-wrapper">
                            <div className="row g-4">
                                <div className="col-lg-4">
                                    <div className="product-slick">
                                        <div>
                                            <img src={`uploads/${pimg}`} alt=""
                                                className="w-100 img-fluid lazyload" style={{ height: "500px" }} />
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="col-12">
                                            <div className="slider-nav"></div>
                                        </div>
                                    </div>
                                </div>
                                <div className="col-lg-4" style={{ marginLeft: "100px", height: "700px" }}>
                                    <div className="product-page-details product-description-box sticky-details mt-0">
                                        <div className="trending-text ">
                                            <img src="../assets/images/product-details/trending.gif" className="img-fluid" alt="" />
                                            <h5>Product Quantity: {pq}</h5>
                                        </div>

                                        <h2 className="main-title">Product Name: {pname}</h2>

                                        <div className="price-text">
                                            <h3><span className="fw-normal">MRP:</span>
                                                â‚¹ {pprice}
                                            </h3><span>Inclusive all the text</span>
                                        </div>

                                        <div className="accordion accordion-flush product-accordion" id="accordionFlushExample">
                                            <div className="accordion-item">
                                                <h2 className="accordion-header">
                                                    <button className="accordion-button collapsed" type="button"
                                                        data-bs-toggle="collapse" data-bs-target="#flush-collapseOne"
                                                        aria-expanded="false" aria-controls="flush-collapseOne">
                                                        Product Description</button>
                                                </h2>
                                                <div id="flush-collapseOne" className="accordion-collapse collapse"
                                                    data-bs-parent="#accordionFlushExample">
                                                    <div className="accordion-body">
                                                        <p>{pdes}</p>
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="accordion-item">
                                                <h2 className="accordion-header">
                                                    <button className="accordion-button" type="button" data-bs-toggle="collapse"
                                                        data-bs-target="#flush-collapseTwo" aria-expanded="false"
                                                        aria-controls="flush-collapseTwo">
                                                        Information</button>
                                                </h2>
                                                <div id="flush-collapseTwo" className="accordion-collapse collapse show"
                                                    data-bs-parent="#accordionFlushExample">
                                                    <div className="accordion-body">
                                                        <div className="bordered-box border-0 mt-0 pt-0">
                                                            <h4 className="sub-title">product Info</h4>
                                                            <ul className="shipping-info">
                                                                <li><span>SKU: </span>SP18 (COPY)</li>
                                                                <li><span>Unit: </span>1 Item</li>
                                                                <li><span>Weight: </span>150 Gms</li>
                                                                <li><span>Stock Status: </span>In stock</li>
                                                                <li><span>Quantity: </span>40 Items Left</li>
                                                            </ul>
                                                        </div>
                                                        <button className="btn btn-solid" style={{ marginTop: "100px", marginLeft: "-90px" }} onClick={addcart}>Add to cart</button>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    )
}

export default Productdetails
