import React, { useEffect, useState } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';

const ProductDetails = () => {
    const [param] = useSearchParams();
    const id = param.get("id");
    const [product, setProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);

    const navigate = useNavigate();

    useEffect(() => {
        fetch(`http://localhost:9000/api/product/${id}`)
            .then(res => res.json())
            .then(data => setProduct(data))
            .catch(err => console.error("Error fetching product:", err));
    }, [id]);

    const handleAddToCart = async () => {
        const userinfo = JSON.parse(sessionStorage.getItem("userinfo"));
        const token = JSON.parse(sessionStorage.getItem("token"));

        if (!userinfo || !token) {
            Swal.fire({
                icon: 'info',
                title: 'Login Required',
                text: 'Please log in before adding to cart.',
                showConfirmButton: true
            });
            navigate(`/login?idd=${id}`);
            return;
        }

        const { email, userid } = userinfo;

        // Check if already in cart
        const checkRes = await fetch(`http://localhost:9000/api/checkcart?email=${email}&idd=${id}`);
        const checkData = await checkRes.json();

        if (checkData.statuscode === 2) {
            Swal.fire({
                icon: 'warning',
                title: 'Already in Cart',
                text: 'This product is already in your cart.'
            });
            return;
        }

        const payload = {
            userid,
            email,
            pname: product.name,
            pq: quantity,
            pprice: product.price,
            pimg: product.image,
            pdes: product.description,
            tprice: quantity * product.price,
            id: product._id
        };

        try {
            const res = await fetch("http://localhost:9000/api/addcart", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(payload)
            });

            const result = await res.json();

            if (result.statuscode === 1) {
                Swal.fire({
                    icon: 'success',
                    title: 'Added to Cart',
                    text: 'Product has been added to your cart.',
                    timer: 2000,
                    showConfirmButton: false
                });
            } else {
                Swal.fire({
                    icon: 'error',
                    title: 'Error',
                    text: 'Failed to add to cart.'
                });
            }
        } catch (error) {
            console.error("Error adding to cart:", error);
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: 'Something went wrong while adding to cart.'
            });
        }
    };

    if (!product) return <div>Loading...</div>;

    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-md-6">
                    <img src={product.image} alt={product.name} className="img-fluid" />
                </div>
                <div className="col-md-6">
                    <h2>{product.name}</h2>
                    <p>{product.description}</p>
                    <h4>â‚¹{product.price}</h4>

                    <div className="form-group mt-3">
                        <label>Quantity:</label>
                        <input
                            type="number"
                            className="form-control"
                            value={quantity}
                            onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value)))}
                            style={{ width: "100px" }}
                        />
                    </div>

                    <button className="btn btn-primary mt-3" onClick={handleAddToCart}>
                        Add to Cart
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ProductDetails;
