import React, { useContext, useEffect, useState } from 'react';
import context from './usecontext';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { useSelector } from 'react-redux';

const Addtocart = () => {
  const [cartdata, setcartdata] = useState([]);
  const { orderid, setorderid } = useContext(context);
  const [tprice, settprice] = useState(0);
  const navigate = useNavigate();

  const { Loggedin, userId } = useSelector((state) => state.userslice);

  useEffect(() => {
    if (Loggedin && userId) {
      addcart();
    } else {
      navigate('/login');
    }
  }, [Loggedin, userId]);

  const grandtotal = (cart) => {
    const total = cart.reduce((acc, item) => acc + item.productprice * item.qnt, 0);
    settprice(total);
    sessionStorage.setItem('billamt', JSON.stringify(total));
  };

  const addcart = async () => {
    try {
      const res = await fetch(`http://localhost:9000/api/cartdata/${userId}`, {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${sessionStorage.getItem('token')}`,
        },
      });

      if (res.ok) {
        const result = await res.json();
        if (result.statuscode === 1) {
          const updated = result.data.map((item) => ({
            ...item,
            qnt: item.pq || 1,
          }));
          setcartdata(updated);
          grandtotal(updated);
        } else {
          setcartdata([]);
          settprice(0);
        }
      }
    } catch (err) {
      console.error(err);
      alert('Error fetching cart data');
    }
  };

  const updateCartQuantity = async (productId, quantity) => {
    try {
      const res = await fetch('http://localhost:9000/api/updatecartquantity', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${sessionStorage.getItem('token')}`,
        },
        body: JSON.stringify({ productId, quantity }),
      });

      if (!res.ok) {
        console.error('Failed to update cart quantity');
      }
    } catch (err) {
      console.error('Error updating cart quantity:', err);
    }
  };

  const increament1 = (index) => {
    const updated = [...cartdata];
    updated[index].qnt += 1;
    setcartdata(updated);
    grandtotal(updated);
    updateCartQuantity(updated[index]._id, updated[index].qnt);
  };

  const decreament1 = (index) => {
    const updated = [...cartdata];
    if (updated[index].qnt > 1) {
      updated[index].qnt -= 1;
      setcartdata(updated);
      grandtotal(updated);
      updateCartQuantity(updated[index]._id, updated[index].qnt);
    }
  };

  const del = async (id) => {
    const result = await Swal.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#ec8951',
      cancelButtonColor: '#212121',
      confirmButtonText: 'Yes, delete it!',
    });

    if (!result.isConfirmed) return;

    try {
      const res = await fetch(`http://localhost:9000/api/cartdelete/${id}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${sessionStorage.getItem('token')}`,
        },
      });

      if (res.ok) {
        const result = await res.json();
        if (result.statuscode === 1) {
          Swal.fire('Deleted!', 'Your product has been deleted.', 'success');
          addcart();
        }
      }
    } catch (err) {
      Swal.fire('Error', err.message, 'error');
    }
  };

  const checkout = () => {
    if (cartdata.length === 0) {
      Swal.fire('Your cart is empty!', 'Please add some products before checkout.', 'warning');
      return;
    }

    const orderId = Math.floor(Math.random() * 9000000) + 1000000;
    setorderid(orderId);
    navigate('/checkout');
  };

  return (
    <div>
      <section className="cart-section section-b-space">
        <div className="container">
          <div className="cart_counter">
            <div className="countdownholder">
              Your cart will be expired in <span id="timer"></span> minutes!
            </div>
            <a href="" className="cart_checkout btn btn-solid btn-xs">
              check out
            </a>
          </div>

          <div className="table-responsive">
            <table className="table cart-table">
              <thead>
                <tr className="table-head">
                  <th>image</th>
                  <th>product name</th>
                  <th>price</th>
                  <th>quantity</th>
                  <th>total</th>
                  <th>action</th>
                </tr>
              </thead>
              <tbody>
                {cartdata.length > 0 ? (
                  cartdata.map((dd, i) => (
                    <tr key={dd._id}>
                      <td>
                        <img src={`uploads/${dd.productimage}`} className="img-fluid" alt="" />
                      </td>
                      <td>
                        <a href="">{dd.productname}</a>
                      </td>
                      <td className="table-price">
                        <h2>₹{dd.productprice}</h2>
                      </td>
                      <td>
                        <div className="qty-box">
                          <div className="input-group qty-container">
                            <button className="btn qty-btn-minus" onClick={() => decreament1(i)}>
                              <i className="ri-arrow-left-s-line"></i>
                            </button>
                            <input
                              type="number"
                              readOnly
                              name="qty"
                              className="form-control input-qty"
                              value={dd.qnt}
                            />
                            <button className="btn qty-btn-plus" onClick={() => increament1(i)}>
                              <i className="ri-arrow-right-s-line"></i>
                            </button>
                          </div>
                        </div>
                      </td>
                      <td>
                        <h2 className="td-color">₹{dd.productprice * dd.qnt}</h2>
                      </td>
                      <td>
                        <a className="icon remove-btn" onClick={() => del(dd._id)}>
                          <i className="ri-close-line"></i>
                        </a>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="6" className="text-center py-5">
                      Your cart is empty. Please add some products.
                    </td>
                  </tr>
                )}
              </tbody>
              {cartdata.length > 0 && (
                <tfoot>
                  <tr>
                    <td colSpan="4" className="d-md-table-cell d-none">
                      total price :
                    </td>
                    <td className="d-md-none">total price :</td>
                    <td>
                      <h2>₹{tprice}</h2>
                    </td>
                  </tr>
                </tfoot>
              )}
            </table>
          </div>

          <div className="row cart-buttons">
            <div className="col-3">
              <a href="/products" className="btn btn-solid text-capitalize">
                continue shopping
              </a>
            </div>
            <div className="col-6"></div>
            {cartdata.length > 0 && (
              <div className="col-3">
                <button className="btn btn-solid text-capitalize" onClick={checkout}>
                  check out
                </button>
              </div>
            )}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Addtocart;
