import React, { useState } from 'react';
import { Link, useNavigate, useSearchParams } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import Swal from 'sweetalert2';
import { Login } from './reducer/userslice';

const Loginp = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();
    const [searchParams] = useSearchParams();
    const productId = searchParams.get("id");
    const dispatch = useDispatch();

    const validateForm = () => {
        const errors = {};
        const emailRegex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;

        if (!email) {
            errors.email = "Email is required.";
        } else if (!emailRegex.test(email)) {
            errors.email = "Please enter a valid email address.";
        }

        if (!password) {
            errors.password = "Password is required.";
        }

        if (Object.keys(errors).length > 0) {
            Swal.fire({
                icon: 'error',
                title: 'Validation Error',
                html: `<ul style="text-align: left;">
                        ${Object.values(errors).map(err => `<li>${err}</li>`).join('')}
                       </ul>`
            });
            return false;
        }

        return true;
    };

    const handleLogin = async (e) => {
        e.preventDefault();

        if (!validateForm()) return;

        const loginData = { email, pwd: password };

        try {
            const response = await fetch("http://localhost:9000/api/login", {
                method: "POST",
                body: JSON.stringify(loginData),
                headers: { 
                    'Content-Type': 'application/json;charset=utf-8' 
                },
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const result = await response.json();

            if (result.statuscode === 1) {
                await Swal.fire({
                    icon: 'success',
                    title: 'Login Successful',
                    text: 'You have successfully logged in!',
                    timer: 2000,
                    showConfirmButton: false
                });

                // Store user data and token
                dispatch(Login(result.userdata));
                sessionStorage.setItem("userinfo", JSON.stringify(result.userdata));
                sessionStorage.setItem("token", JSON.stringify(result.authtoken));

                // Redirect based on user type or product ID
                if (productId) {
                    navigate(`/productdetails?id=${productId}`);
                } else if (result.userdata.usertype === "admin") {
                    navigate("/home");
                } else {
                    navigate("/");
                }
            } else {
                Swal.fire({
                    icon: 'error',
                    title: 'Login Failed',
                    text: result.message || 'Invalid email or password',
                    confirmButtonText: 'OK'
                });
            }
        } catch (error) {
            console.error('Login error:', error);
            Swal.fire({
                icon: 'error',
                title: 'Login Failed',
                text: 'An error occurred during login. Please try again.',
                confirmButtonText: 'OK'
            });
        }
    };

    return (
        <div>
            <section className="login-page section-b-space">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-6">
                            <h3>Login</h3>
                            <div className="theme-card">
                                <form className="theme-form" onSubmit={handleLogin}>
                                    <div className="form-box">
                                        <label htmlFor="email" className="form-label">Email</label>
                                        <input
                                            type="email"
                                            className="form-control"
                                            id="email"
                                            placeholder="Email"
                                            value={email}
                                            onChange={(e) => setEmail(e.target.value)}
                                            required
                                        />
                                    </div>
                                    <div className="form-box">
                                        <label htmlFor="password" className="form-label">Password</label>
                                        <input
                                            type="password"
                                            className="form-control"
                                            id="password"
                                            placeholder="Enter your password"
                                            value={password}
                                            onChange={(e) => setPassword(e.target.value)}
                                            required
                                        />
                                    </div>
                                    <button type="submit" className="btn btn-solid">Login</button>
                                </form>
                            </div>
                        </div>
                        <div className="col-lg-6 right-login">
                            <h3>New Customer</h3>
                            <div className="theme-card authentication-right">
                                <h6 className="title-font">Create An Account</h6>
                                <p>
                                    Sign up for a free account at our store. Registration is quick and easy.
                                    To start shopping click register.
                                </p>
                                <Link to="/register" className="btn btn-solid">Create an Account</Link>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    );
};

export default Loginp;
