import React, { useContext, useEffect, useState } from 'react'
import context from './usecontext'
import { useNavigate } from 'react-router-dom'
import Swal from 'sweetalert2'
import { useSelector } from "react-redux";

const Addtocart = () => {
  const [cartdata, setcartdata] = useState([])
  const [cartlen, setcartlen] = useState([])
  const { orderid, setorderid } = useContext(context) 
  const [tprice, settprice] = useState(0)
  const navigate = useNavigate()
  const { Loggedin, email } = useSelector((state) => {
		return state.userslice
	})

  useEffect(() => { 
    if (Loggedin) {
      addcart()
    } else {
      navigate('/login')
    }
  }, [Loggedin])
  useEffect(() => {
		var carttotal = 0
		for (var i = 0; i < cartlen.length; i++) {
			carttotal = carttotal + cartlen[i].tcost
		}
		setcartlen(carttotal)
		sessionStorage.setItem("billamt", JSON.stringify(carttotal))
	}, [cartlen])

  const increament1 = (index) => {
    const update = [...cartdata]
    update[index].qnt += 1
    setcartdata(update)
    grandtotal(update)
    updateCartQuantity(update[index]._id, update[index].qnt) 
  }

  const grandtotal = (cart) => {
    const totalprice = cart.reduce((acc, data) => acc + data.productprice * data.qnt, 0)
    settprice(totalprice)
  }

  const decreament1 = (index) => {
    const update = [...cartdata]
    if (update[index].qnt > 1) {
      update[index].qnt -= 1
      setcartdata(update)
      grandtotal(update)
      updateCartQuantity(update[index]._id, update[index].qnt) 
    }
  }

 const fetchCartData = async () => {
  try {
    // 1. Get authentication data
    const email = sessionStorage.getItem('email');
    const token = sessionStorage.getItem('token');
    
    // 2. Validate authentication data
    if (!email) {
      await Swal.fire({
        icon: 'warning',
        title: 'Email Missing',
        text: 'Please login again',
        confirmButtonText: 'OK'
      });
      navigate('/login');
      return;
    }

    if (!token) {
      await Swal.fire({
        icon: 'warning',
        title: 'Session Expired',
        text: 'Please login again',
        confirmButtonText: 'OK'
      });
      navigate('/login');
      return;
    }

    // 3. Make API request
    const response = await fetch(`http://localhost:9000/api/cartdata/${email}`, {
      method: "GET",
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      },
      credentials: 'include' // If using cookies
    });

    // 4. Handle response
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const result = await response.json();

    if (result.statuscode === 1) {
      setcartdata(result.data);
      setcartlen(result.arraylength);
    } else {
      // No items is a valid state
      setcartdata([]);
      setcartlen(0);
      console.log('Cart is empty:', result.message);
    }
  } catch (err) {
    console.error('Fetch Error:', err);
    Swal.fire({
      icon: 'error',
      title: 'Error',
      text: err.message || 'Failed to fetch cart data',
      confirmButtonText: 'OK'
    });
    
    // If unauthorized, clear storage and redirect
    if (err.message.includes('401')) {
      sessionStorage.clear();
      navigate('/login');
    }
  }
};




  const updateCartQuantity = async (productId, quantity) => {
    try {
      const res = await fetch(`http://localhost:9000/api/updatecartquantity`, {
        method: "PUT",
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${sessionStorage.getItem('token')}`
        },
        body: JSON.stringify({
          // userId: user._id,
          productId,
          quantity
        })
      })
      if (!res.ok) {
        console.error('Failed to update cart quantity')
      }
    } catch (err) {
      console.error('Error updating cart quantity:', err)
    }
  }

  const checkout = () => {
    if (cartdata.length === 0) {
      Swal.fire('Your cart is empty!', 'Please add some products before checkout.', 'warning')
      return
    }
    
    var r = Math.floor(Math.random() * 9000000) + 1000000
    setorderid(r)
    navigate("/checkout")
  }

  const del = async (id) => {
    const result = await Swal.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#ec8951',
      cancelButtonColor: '#212121',
      confirmButtonText: 'Yes, delete it!'
    });
    
    if (!result.isConfirmed) return;

    try {
      const res = await fetch(`http://localhost:9000/api/cartdelete/${id}`, {
        method: "delete",
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          // userId: user._id
        })
      })
      if (res.ok) {
        const result = await res.json()
        if (result.statuscode === 1) {
          Swal.fire('Deleted!', 'Your product has been deleted.', 'success')
          addcart();
        }
      }
    } catch (err) {
      Swal.fire('Error', err, 'error')
    }
  }

  return (
    <div>
      <section className="cart-section section-b-space">
        <div className="container">
          <div className="cart_counter">
            <div className="countdownholder">
              Your cart will be expired in<span id="timer"></span> minutes!
            </div>
            <a href="" className="cart_checkout btn btn-solid btn-xs">check out</a>
          </div> 
          <div className="table-responsive">
            <table className="table cart-table">
              <thead>
                <tr className="table-head">
                  <th>image</th>
                  <th>product name</th>
                  <th>price</th>
                  <th>quantity</th>
                  <th>total</th>
                  <th>action</th>
                </tr>
              </thead>
              <tbody>
                {
                  cartdata.length > 0 ? (
                    cartdata.map((dd, i) => (
                      <tr key={dd._id}>
                        <td>
                          <a href="">
                            <img src={`uploads/${dd.productimage}`} className="img-fluid" alt=""/>
                          </a>
                        </td>
                        <td>
                          <a href="">{dd.productname}</a>
                          <div className="mobile-cart-content row">
                            <div className="col">
                              <div className="qty-box">
                                <div className="input-group qty-container">
                                  <button className="btn qty-btn-minus">
                                    <i className="ri-arrow-left-s-line"></i>
                                  </button>
                                  <input type="number" readOnly name="qty"
                                    className="form-control input-qty" value="1"/>
                                  <button className="btn qty-btn-plus">
                                    <i className="ri-arrow-right-s-line"></i>
                                  </button>
                                </div>
                              </div>
                            </div>
                            <div className="col table-price">
                              <h2 className="td-color">₹{dd.productprice}</h2>
                            </div>
                            <div className="col">
                              <h2 className="td-color">
                                <a href="" className="icon remove-btn">
                                  <i className="ri-close-line"></i>
                                </a>
                              </h2>
                            </div>
                          </div>
                        </td>
                        <td className="table-price">
                          <h2>₹{dd.productprice}</h2>
                        </td>
                        <td>
                          <div className="qty-box">
                            <div className="input-group qty-container">
                              <button className="btn qty-btn-minus">
                                <i className="ri-arrow-left-s-line" onClick={() => decreament1(i)}></i>
                              </button>
                              {dd.qnt}
                              <button className="btn qty-btn-plus">
                                <i className="ri-arrow-right-s-line" onClick={() => increament1(i)}></i>
                              </button>
                            </div>
                          </div>
                        </td>
                        <td>
                          <h2 className="td-color">₹{dd.productprice * dd.qnt}</h2>
                        </td>
                        <td>
                          <a className="icon remove-btn" onClick={() => del(dd._id)}>
                            <i className="ri-close-line"></i>
                          </a>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="6" className="text-center py-5">
                        Your cart is empty. Please add some products.
                      </td>
                    </tr>
                  )
                }
              </tbody>
              {cartdata.length > 0 && (
                <tfoot>
                  <tr>
                    <td colSpan="4" className="d-md-table-cell d-none">total price :</td>
                    <td className="d-md-none">total price :</td>
                    <td>
                      <h2>₹{tprice}</h2>
                    </td>
                  </tr>
                </tfoot>
              )}
            </table>
          </div>

          <div className="row cart-buttons">
            <div className="col-3">
              <a href="/products" className="btn btn-solid text-capitalize">continue shopping</a>
            </div>
            <div className="col-6"></div>
            {cartdata.length > 0 && (
              <div className="col-3">
                <button className="btn btn-solid text-capitalize" onClick={checkout}>check out</button>
              </div>
            )}
          </div>
        </div>
      </section>
    </div>
  )
}

export default Addtocart
