import React, { useContext, useEffect, useState } from 'react'
import context from './usecontext'
import { useNavigate } from 'react-router-dom'
import Swal from 'sweetalert2'

const Addtocart = () => {
 const[cartdata,setcartdata]=useState([])
 const{orderid,setorderid}=useContext(context)
 const[tprice,settprice]=useState()

 const navigate=useNavigate()
  useEffect(()=>{
    addcart()

  },[])
  const increament1=(index)=>{
const update=[...cartdata]
update[index].qnt+=1
setcartdata(update)
grandtotal(update)
  }
  const grandtotal=(cart)=>{
    const totalprice=cart.reduce((acc,data)=>acc+data.productprice*data.qnt,0)
    settprice(totalprice)
  
}
  const decreament1=(index)=>{
    const update=[...cartdata]
    if(update[index].qnt>1){
    update[index].qnt-=1
        setcartdata(update)
        grandtotal(update)
    }
    
    

  }
  const addcart=async()=>{
    try{
    const res=await fetch("http://localhost:9000/api/cartdata",{
      method:"get"
    })
    if(res.ok){
      const result=await res.json()
      if(result.statuscode===1){
       
        const updatecart=result.data.map(item=>({
...item,
qnt:item.productquantity ||1
        })
        
        )
        setcartdata(updatecart)
        grandtotal(updatecart)
      }
      else{
        alert("data didn't fetch")
      }
    }
  }catch(err){
    alert(err)

  }

  }
  const checkout=()=>{
    var r=Math.random(100000,10000000)
    setorderid(r)

    
    navigate("/checkout")
    

  }

  const del = async (id) => {
          const result = await Swal.fire({
              title: 'Are you sure?',
              text: "You won't be able to revert this!",
              icon: 'warning',
              showCancelButton: true,
              confirmButtonColor: '#ec8951',
              cancelButtonColor: '#212121',
              confirmButtonText: 'Yes, delete it!'
          });
          
          if (!result.isConfirmed) return;
      
          try {
              const res = await fetch(`http://localhost:9000/api/cartdelete/${id}`, {
                  method: "delete"
              })
              if (res.ok) {
                  const result = await res.json()
                  if (result.statuscode === 1) {
                     
                      Swal.fire('Deleted!', 'Your product has been deleted.', 'success')
                      addcart()
                  }
              }
          } catch (err) {
              Swal.fire('Error', err, 'error')
          }
      }
  return (
    <div>
        <section class="cart-section section-b-space">
        <div class="container">
            <div class="cart_counter">
                <div class="countdownholder">
                    Your cart will be expired in<span id="timer"></span> minutes!
                </div>
                <a href="" class="cart_checkout btn btn-solid btn-xs">check out</a>
            </div> 
            <div class="table-responsive">
                <table class="table cart-table">
                    <thead>
                        <tr class="table-head">
                            <th>image</th>
                            <th>product name</th>
                            <th>price</th>
                            <th>quantity</th>
                            <th>total</th>
                            <th>action</th>
                        </tr>
                    </thead>
                    <tbody>
                
                          {
                          cartdata.length>0?<>
                          {
                            cartdata.map((dd,i)=>
                            <>
                                    <tr>
                            <td>
                                <a href="">
                                    <img src={`uploads/${dd.productimage}`} class="img-fluid" alt=""/>
                                </a>
                            </td>
                            <td>
                                <a href="">{dd.productname}</a>
                                <div class="mobile-cart-content row">
                                    <div class="col">
                                        <div class="qty-box">
                                            <div class="input-group qty-container">
                                                <button class="btn qty-btn-minus">
                                                    <i class="ri-arrow-left-s-line"></i>
                                                </button>
                                                <input type="number" readonly="" name="qty"
                                                    class="form-control input-qty" value="1"/>
                                                <button class="btn qty-btn-plus">
                                                    <i class="ri-arrow-right-s-line"></i>
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                    <div class="col table-price">
                                        <h2 class="td-color">₹{dd.productprice}</h2>
                                    </div>
                                    <div class="col">
                                        <h2 class="td-color">
                                            <a href="" class="icon remove-btn">
                                                <i class="ri-close-line"></i>
                                            </a>
                                        </h2>
                                    </div>
                                </div>
                            </td>
                            <td class="table-price">
                                <h2>₹{dd.productprice}</h2>
                            </td>
                            <td>
                                <div class="qty-box">
                                    <div class="input-group qty-container">
                                        <button class="btn qty-btn-minus">
                                            <i class="ri-arrow-left-s-line" onClick={()=>decreament1(i)}></i>
                                        </button>
                                        {/* <input type="number" readonly="" name="qty" class="form-control input-qty"
                                            value="1"/> */}
                                        {dd.qnt}
                                        <button class="btn qty-btn-plus">
                                            <i class="ri-arrow-right-s-line" onClick={()=>increament1(i)}></i>
                                        </button>
                                    </div>
                                </div>
                            </td>
                            <td>
                                <h2 class="td-color">₹{dd.productprice*dd.qnt}</h2>
                            </td>
                            <td>
                                <a  class="icon remove-btn" onClick={()=>del(dd._id)}>
                                    <i class="ri-close-line" ></i>
                                </a>
                            </td>
                            </tr>
                            </>
                            )
                            
                          }
                            </>:null
                          }
                  
                    </tbody>
                   
                    <tfoot>
                        <tr>
                            <td colspan="4" class="d-md-table-cell d-none">total price :</td>
                            <td class="d-md-none">total price :</td>
                            <td>
                                <h2>₹{tprice}</h2>
                            </td>
                        </tr>
                    </tfoot> 
                </table>
            </div>

            <div class="row cart-buttons">
                <div class="col-3">
                    <a href="" class="btn btn-solid text-capitalize">continue
                        shopping</a>
                       
                </div>
                <div class="col-6">
                   
                </div>
                <div class="col-3">
                    <button class="btn btn-solid text-capitalize" onClick={checkout}>check out</button>
                </div>
            </div>
        </div>
    </section>
     
    </div>
  )
}

