import React, { useContext, useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';
import 'remixicon/fonts/remixicon.css';
import { userLogout } from './reducer/userslice';
import context from './usecontext';

const Userheader = () => {
    const [info, setInfo] = useState(null);
    const { setUsertype } = useContext(context);

    // Safe Redux state access with defaults
    const userState = useSelector((state) => state.user) || {};
    const {
        isLoggedIn = false,
        userId = null,
        username = "Guest",
        firstName = ""
    } = userState;

    const navigate = useNavigate();
    const dispatch = useDispatch();

    useEffect(() => {
        const userData = sessionStorage.getItem('userinfo');
        if (userData) {
            try {
                setInfo(JSON.parse(userData));
            } catch (error) {
                console.error("Error parsing user data:", error);
                sessionStorage.removeItem('userinfo');
            }
        }
    }, [isLoggedIn]);

    const onLogout = () => {
        dispatch(userLogout());
        sessionStorage.clear();
        localStorage.clear();
        setUsertype(null);
        navigate("/login");
    };

    // Get display name with fallbacks
    const getDisplayName = () => {
        return info?.firstname || firstName || username || "User";
    };

    return (
        <header>
            <div className="top-header">
                <div className="mobile-fix-option"></div>
                <div className="container">
                    <div className="row">
                        <div className="col-lg-6">
                            <div className="header-contact">
                                <ul>
                                    <li>
                                        Welcome{" "}
                                        <span style={{ color: "#ec8951" }}>
                                            {isLoggedIn ? getDisplayName() : "Guest"}
                                        </span>{" "}
                                        to Our store Bakery
                                    </li>
                                    <li>
                                        <i className="ri-phone-fill"></i>
                                        Call Us: 123 - 456 - 7890
                                    </li>
                                </ul>
                            </div>
                        </div>
                        <div className="col-lg-6 text-end">
                            <ul className="header-dropdown">
                                <li className="onhover-dropdown mobile-account">
                                    <i className="ri-user-fill"></i>
                                    My Account
                                    <ul className="onhover-show-div">
                                        {isLoggedIn ? (
                                            <li onClick={onLogout}>
                                                <Link to="#">Logout</Link>
                                            </li>
                                        ) : (
                                            <>
                                                <li>
                                                    <Link to="/login">Login</Link>
                                                </li>
                                                <li>
                                                    <Link to="/register">Register</Link>
                                                </li>
                                            </>
                                        )}
                                    </ul>
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
            {/* Rest of your JSX remains the same */}
        </header>
    );
};

export default Userheader;
