import { createSlice } from "@reduxjs/toolkit"

const state={
    Loggedin:false,
    username:"aman@gmail.com",
    personname:""
}
const userslice=createSlice({
name:"userslice",
initialState:state,
reducers:{
    Login:(state,action)=>{
state.Loggedin=true
state.username=action.payload.uname
state.personname=action.payload.pname
    },
    Logout:(state,action)=>{
    state.Loggedin=false
    state.username=""
    state.personname=""
},
}

})
export const{Login,Logout}=userslice.actions
export default userslice.reducer
