import React, { useContext, useEffect, useState } from 'react'
import context from './usecontext'
import { useNavigate } from 'react-router-dom'
import Swal from 'sweetalert2'
import { useSelector } from "react-redux";

const Addtocart = () => {
  const [cartdata, setcartdata] = useState([])
  const { orderid, setorderid } = useContext(context) 
  const [tprice, settprice] = useState(0)
  const navigate = useNavigate()
  const { Loggedin, userid } = useSelector((state) => {
    return state.userslice
  })

  useEffect(() => { 
    if (Loggedin) {
      fetchCartItems()
    } else {
      navigate('/login')
    }
  }, [Loggedin, userid])

  // Fetch cart items for the current user
  const fetchCartItems = async () => {
    try {
      const res = await fetch(`http://localhost:9000/api/cartdata/${userid}`)
      if (!res.ok) throw new Error("Failed to fetch cart items")
      
      const result = await res.json()
      if (result.statuscode === 1) {
        // Add quantity field to each cart item if not present
        const itemsWithQuantity = result.data.map(item => ({
          ...item,
          qnt: item.productquantity || 1
        }))
        setcartdata(itemsWithQuantity)
      } else {
        setcartdata([])
      }
    } catch (err) {
      console.error("Error fetching cart items:", err)
      Swal.fire('Error', 'Failed to load cart items', 'error')
    }
  }

  // Calculate total price whenever cartdata changes
  useEffect(() => {
    const total = cartdata.reduce((acc, item) => acc + (item.productprice * item.qnt), 0)
    settprice(total)
    sessionStorage.setItem("billamt", JSON.stringify(total))
  }, [cartdata])

  const incrementQuantity = (index) => {
    const updatedCart = [...cartdata]
    updatedCart[index].qnt += 1
    setcartdata(updatedCart)
    updateCartItem(updatedCart[index])
  }

  const decrementQuantity = (index) => {
    const updatedCart = [...cartdata]
    if (updatedCart[index].qnt > 1) {
      updatedCart[index].qnt -= 1
      setcartdata(updatedCart)
      updateCartItem(updatedCart[index])
    }
  }

  // Update cart item quantity on server
  const updateCartItem = async (item) => {
    try {
      const res = await fetch(`http://localhost:9000/api/updatecart`, {
        method: "PUT",
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${sessionStorage.getItem('token')}`
        },
        body: JSON.stringify({
          userid: userid,
          productid: item.productid,
          newQuantity: item.qnt,
          productprice: item.productprice
        })
      })
      
      if (!res.ok) {
        throw new Error('Failed to update cart quantity')
      }
    } catch (err) {
      console.error('Error updating cart:', err)
      Swal.fire('Error', 'Failed to update cart quantity', 'error')
      // Revert to previous state on error
      fetchCartItems()
    }
  }

  const checkout = () => {
    if (cartdata.length === 0) {
      Swal.fire('Your cart is empty!', 'Please add some products before checkout.', 'warning')
      return
    }
    
    var r = Math.floor(Math.random() * 9000000) + 1000000
    setorderid(r)
    navigate("/checkout")
  }

  const deleteCartItem = async (id) => {
    const result = await Swal.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#ec8951',
      cancelButtonColor: '#212121',
      confirmButtonText: 'Yes, delete it!'
    });
    
    if (!result.isConfirmed) return;

    try {
      const res = await fetch(`http://localhost:9000/api/cartdelete/${id}`, {
        method: "DELETE",
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${sessionStorage.getItem('token')}`
        }
      })
      
      if (res.ok) {
        const result = await res.json()
        if (result.statuscode === 1) {
          Swal.fire('Deleted!', 'Your product has been deleted.', 'success')
          fetchCartItems()
        }
      } else {
        throw new Error('Failed to delete item')
      }
    } catch (err) {
      Swal.fire('Error', err.message || 'Failed to delete item', 'error')
    }
  }

  return (
    <div>
      <section className="cart-section section-b-space">
        <div className="container">
          <div className="cart_counter">
            <div className="countdownholder">
              Your cart will be expired in<span id="timer"></span> minutes!
            </div>
            <a href="" className="cart_checkout btn btn-solid btn-xs">check out</a>
          </div> 
          <div className="table-responsive">
            <table className="table cart-table">
              <thead>
                <tr className="table-head">
                  <th>image</th>
                  <th>product name</th>
                  <th>price</th>
                  <th>quantity</th>
                  <th>total</th>
                  <th>action</th>
                </tr>
              </thead>
              <tbody>
                {
                  cartdata.length > 0 ? (
                    cartdata.map((item, index) => (
                      <tr key={item._id}>
                        <td>
                          <a href="">
                            <img src={`uploads/${item.productimage}`} className="img-fluid" alt=""/>
                          </a>
                        </td>
                        <td>
                          <a href="">{item.productname}</a>
                          <div className="mobile-cart-content row">
                            <div className="col">
                              <div className="qty-box">
                                <div className="input-group qty-container">
                                  <button className="btn qty-btn-minus">
                                    <i className="ri-arrow-left-s-line"></i>
                                  </button>
                                  <input type="number" readOnly name="qty"
                                    className="form-control input-qty" value="1"/>
                                  <button className="btn qty-btn-plus">
                                    <i className="ri-arrow-right-s-line"></i>
                                  </button>
                                </div>
                              </div>
                            </div>
                            <div className="col table-price">
                              <h2 className="td-color">₹{item.productprice}</h2>
                            </div>
                            <div className="col">
                              <h2 className="td-color">
                                <a href="" className="icon remove-btn">
                                  <i className="ri-close-line"></i>
                                </a>
                              </h2>
                            </div>
                          </div>
                        </td>
                        <td className="table-price">
                          <h2>₹{item.productprice}</h2>
                        </td>
                        <td>
                          <div className="qty-box">
                            <div className="input-group qty-container">
                              <button 
                                className="btn qty-btn-minus" 
                                onClick={() => decrementQuantity(index)}
                              >
                                <i className="ri-arrow-left-s-line"></i>
                              </button>
                              <input 
                                type="number" 
                                readOnly 
                                name="qty" 
                                className="form-control input-qty" 
                                value={item.qnt} 
                              />
                              <button 
                                className="btn qty-btn-plus" 
                                onClick={() => incrementQuantity(index)}
                              >
                                <i className="ri-arrow-right-s-line"></i>
                              </button>
                            </div>
                          </div>
                        </td>
                        <td>
                          <h2 className="td-color">₹{item.productprice * item.qnt}</h2>
                        </td>
                        <td>
                          <a 
                            className="icon remove-btn" 
                            onClick={() => deleteCartItem(item._id)}
                          >
                            <i className="ri-close-line"></i>
                          </a>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="6" className="text-center py-5">
                        Your cart is empty. Please add some products.
                      </td>
                    </tr>
                  )
                }
              </tbody>
              {cartdata.length > 0 && (
                <tfoot>
                  <tr>
                    <td colSpan="4" className="d-md-table-cell d-none">total price :</td>
                    <td className="d-md-none">total price :</td>
                    <td>
                      <h2>₹{tprice}</h2>
                    </td>
                  </tr>
                </tfoot>
              )}
            </table>
          </div>

          <div className="row cart-buttons">
            <div className="col-3">
              <a href="/products" className="btn btn-solid text-capitalize">continue shopping</a>
            </div>
            <div className="col-6"></div>
            {cartdata.length > 0 && (
              <div className="col-3">
                <button 
                  className="btn btn-solid text-capitalize" 
                  onClick={checkout}
                >
                  check out
                </button>
              </div>
            )}
          </div>
        </div>
      </section>
    </div>
  )
}

export default Addtocart
