import React, { useContext, useEffect, useState } from 'react'
import context from './usecontext'
import { Link, useNavigate } from 'react-router-dom'
import { useSelector } from 'react-redux'
import Swal from 'sweetalert2'

const Login = () => {
    const location=use

    const {fname}=location.state 
    const [email, setemail] = useState()
    const [pwd, setpwd] = useState()
    const navigate = useNavigate()
    
    const { setflag } = useContext(context)
    const { Loggedin } = useSelector((state) => {
        return state.userslice
    })
   
    const show = async () => {
        console.log(Loggedin)
        
        const data1 = { email, pwd }
        try {
            const response1 = await fetch("http://localhost:9000/api/login", {
                method: "post",
                body: JSON.stringify(data1),
                headers: { 'Content-type': 'application/json;charset=utf-8' },
            });
      
            if (response1.ok) {
                const result1 = await response1.json();
       
                if (result1.statuscode === 1) {
                    await Swal.fire({
                        icon: 'success',
                        title: 'Login Successful',
                        text: 'You have successfully logged in!',
                        timer: 2000,
                        showConfirmButton: false
                    });
                    navigate("/ ",{state:{fname}})
                    setflag(result1.usertype)
                }
                else {
                    Swal.fire({
                        icon: 'error',
                        title: 'Registration Required',
                        text: 'Please register first before logging in',
                        confirmButtonText: 'OK'
                    });
                }
            }
        } catch (err) {
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: 'An error occurred during login',
                confirmButtonText: 'OK'
            });
            console.log(err)
        }
    }

    return (
        <div>
            <section class="login-page section-b-space">
                <div class="container">
                    <div class="row">
                        <div class="col-lg-6">
                            <h3>Login</h3>
                            <div class="theme-card">
                                <div class="theme-form">
                                    <div class="form-box">
                                        <label for="email" class="form-label">Email</label>
                                        <input type="text" class="form-control" id="email" placeholder="Email" required="" onChange={(e) => setemail(e.target.value)} />
                                    </div>
                                    <div class="form-box">
                                        <label for="review" class="form-label">Password</label>
                                        <input type="password" class="form-control" id="review"
                                            placeholder="Enter your password" required="" onChange={(e) => setpwd(e.target.value)} />
                                    </div>
                                   <button class="btn btn-solid" onClick={show}>Login</button>
                                </div>
                            </div>
                        </div>
                        <div class="col-lg-6 right-login">
                            <h3>New Customer</h3>
                            <div class="theme-card authentication-right">
                                <h6 class="title-font">Create A Account</h6>
                                <p>Sign up for a free account at our store. Registration is quick and easy. It allows you to be
                                    able to order from our shop. To start shopping click register.</p>
                                <Link to="/register" class="btn btn-solid">Create an Account</Link>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    )
}

export default Login
